import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {

  // constructor() {
  //   super();
  //   this.state = {
  //     articles: [],
  //     loading: false,
  //   };
  // }

  async componentDidMount(){
    let url="https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c22f947b2b124d39a98bf1ae3a7580d1";
    let data=await fetch(url);
    let parseddata=await data.json();
    this.setState({articles:parseddata.articles})

  }
  render() {
    return (
      <div className="container my-3">
        <h1>NewsMonkey-Top Hadline</h1>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title?element.title.slice(0, 45):""}
                  description={element.description?element.description.slice(0, 85):""}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default News;
