import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page:1
    };
  }


  async componentDidMount(){
    let url="https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c22f947b2b124d39a98bf1ae3a7580d1&page=1&pageSize=18";
    let data=await fetch(url);
    let parseddata=await data.json();
    this.setState({articles:parseddata.articles,totalResults:parseddata.totalResults})

  }
  previouspage=async()=>{
    let url=`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c22f947b2b124d39a98bf1ae3a7580d1&page=${this.state.page -1}&pageSize=18`;
    let data=await fetch(url);
    let parseddata=await data.json();
     this.setState({
      page:this.state.page -1,
      articles: parseddata.articles
     })
  }
   nextpage=async()=>{
    if(this.state.page +1>Math.ceil(this.state.totalResults/20)){

    }
    else{
    let url=`https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=c22f947b2b124d39a98bf1ae3a7580d1&page=${this.state.page +1}&pageSize=18`;
    let data=await fetch(url);
    let parseddata=await data.json();
     this.setState({
      page:this.state.page +1,
      articles: parseddata.articles
     })
  }}
  
  render() {
    return (
      <div className="container my-3">
        <h1>NewsMonkey-Top Hadline</h1>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title?element.title.slice(0, 45):""}
                  description={element.description?element.description.slice(0, 85):""}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>

            );
          })}
          <div className="container d-flex justify-content-between">
          <button disabled={this.state.page<=1}type="button"  onClick={this.previouspage} className="btn btn-dark">&larr; Previous</button>
          <button disabled={this.state.page +1>Math.ceil(this.state.totalResults/20)}type="button" onClick={this.nextpage} className="btn btn-dark">Next &rarr;</button>
          </div>
        </div>
      </div>
    );
  }
}

export default News;
